import React, { Component, useState, useEffect } from 'react';
import Customer from './components/Customer';
import CustomerAdd from './components/CustomerAdd';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import CircularProgress from '@material-ui/core/CircularProgress';
import { withStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import { fade } from '@material-ui/core/styles/colorManipulator';
import Typography from '@material-ui/core/Typography';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import CustomerAddF from './components/CustomerAddF';


const styles = theme => ({
  root: {
    width: "100%",
    minWidth: 1080
  },
  menu: {
    marginTop: 15,
    marginBottom: 15,
    display: 'flex',
    justifyContent: 'center'
  },
  paper: {
    marginLeft: 18,
    marginRight: 18
  },
  progress: {
    margin: theme.spacing.unit * 2
  },
  grow: {
    flexGrow: 1,
  },
  tableHead: {
    fontSize: '1.0rem'
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
  },
  title: {
    display: 'none',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing.unit,
      width: 'auto',
    },
  },
  searchIcon: {
    width: theme.spacing.unit * 9,
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
    width: '100%',
  },
  inputInput: {
    paddingTop: theme.spacing.unit,
    paddingRight: theme.spacing.unit,
    paddingBottom: theme.spacing.unit,
    paddingLeft: theme.spacing.unit * 10,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: 120,
      '&:focus': {
        width: 200,
      },
    },
  }
});



function App(props) {

  //state초기화
  const [state, setState] = useState({
    customers: '',
    completed: 0,
    searchKeyword: ''
  });


  const stateRefresh = () => {
    setState({
      customers: '',
      completed: 0,
      searchKeyword: ''
    });

    callApi()
      .then(res => setState({ customers: res }))
      .catch(err => console.log(err));
  }

  //컴포넌트가 마운트된 직후 호출된다.

  useEffect(() => {
    const timer = setInterval(this.progress, 20);
    callApi()
      .then(res => this.setState({ customers: res }))
      .catch(err => console.log(err));
    return () => {
      clearInterval(this.timer);
    }
  }, [])


  //async가 있어야 await 사용가능
  const callApi = async () => {
    //
    const response = await fetch('/api/customers');
    const body = await response.json();
    return body;
  }

  const progress = () => {
    const { completed } = this.state;
    setState({ completed: completed >= 100 ? 0 : completed + 1 });
  };

  const handleValueChange = (e) => {
    const nextState = {};
    nextState[e.target.name] = e.target.value;
    setState(nextState);
  }



  const filteredComponents = (data) => {
    data = data.filter((c) => {
      return c.job.indexOf(this.state.searchKeyword) > -1;
    });
    return data.map((c) => {
      return (
        <Customer
          stateRefresh={this.stateRefresh}
          key={c.id}
          id={c.id}
          name={c.NAME}
          birthday={c.birthday}
          gender={c.gender}
          job={c.job}
        />
      )
    });
  }


  const { classes } = this.props;
  const cellList = ["번호", "이름", "생년월일", "성별", "직업", "설정"];

  return (
    <div className={classes.root}>


      <AppBar>
        <Toolbar>
          <IconButton className={classes.menuButton} color="inherit" aria-label="Open drawer">
            <MenuIcon />
          </IconButton>

          <Typography className={classes.title} variant="h6" color="inherit" noWrap>
            고객 관리 시스템
            </Typography>

          <div className={classes.grow} />
          <div className={classes.search}>
            <div className={classes.searchIcon}>
              <SearchIcon />


            </div>
            <InputBase
              placeholder="검색하기"
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}

              name="searchKeyword"
              value={state.searchKeyword}
              onChange={handleValueChange}
            />
          </div>
        </Toolbar>
      </AppBar>

      <Paper className={classes.paper}>
        <Table>
          <TableHead>
            <TableRow>
              {cellList.map(c => {
                return <TableCell className={classes.tableHead}>{c}</TableCell>
              })}
            </TableRow>
          </TableHead>

          <TableBody>
            {state.customers ?
              filteredComponents(state.customers) :
              <TableRow>
                <TableCell colSpan="6" align="center">
                  <CircularProgress className={classes.progress} variant="determinate" value={state.completed} />
                </TableCell>
              </TableRow>
            }
          </TableBody>
        </Table>
      </Paper>

      <div className={classes.menu}>
        <CustomerAddF stateRefresh={stateRefresh} />
      </div>
    </div>
  );

}

export default withStyles(styles)(App);
