import React, { Component } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import Customer from './components/Customer';
import CustomerAdd from './components/CustomerAdd';

import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import CircularProgress from '@material-ui/core/CircularProgress';
import { withStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import { fade } from '@material-ui/core/styles/colorManipulator';
import Typography from '@material-ui/core/Typography';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';


const GloblaStyle = createGlobalStyle`
  body{
    width: 100%;
    margin: 0 auto;
  }
`;
const styles = theme => ({
  root: {
    width: "100%",
    minWidth: 1080
  },
  menu: {
    marginTop: 15,
    marginBottom: 15,
    display: 'flex',
    justifyContent: 'center'
  },
  paper: {
    marginLeft: 18,
    marginRight: 18
  },
  progress: {
    margin: theme.spacing.unit * 2
  },
  grow: {
    flexGrow: 1,
  },
  tableHead: {
    fontSize: '1.0rem'
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
  },
  title: {
    display: 'none',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing.unit,
      width: 'auto',
    },
  },
  searchIcon: {
    width: theme.spacing.unit * 9,
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
    width: '100%',
  },
  inputInput: {
    paddingTop: theme.spacing.unit,
    paddingRight: theme.spacing.unit,
    paddingBottom: theme.spacing.unit,
    paddingLeft: theme.spacing.unit * 10,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: 120,
      '&:focus': {
        width: 200,
      },
    },
  }
});

class App extends Component {

  constructor(props) {

    super(props);

    //state초기화
    this.state = {
      customers: '',
      completed: 0
    }

    this.stateRefresh = this.stateRefresh.bind(this);

  }


  stateRefresh() {
    this.setState({
      customers: '',
      completed: 0
    });

    this.callApi()
      .then(res => this.setState({ customers: res }))
      .catch(err => console.log(err));
  }

  componentDidMount() {
    this.timer = setInterval(this.progress, 20);
    this.callApi()
    .then(res => this.setState({customers: res}))
    .catch(err => console.log(err));
    }

  //컴포넌트가 마운트된 직후 호출된다.
  componentDidMount() {
    //callApi 호출
    this.callApi()
      .then(res => this.setState({ customers: res }))
      .catch(err => console.log(err));
  }

  //async가 있어야 await 사용가능
  callApi = async () => {
    //
    const response = await fetch('/api/customers');
    const body = await response.json();
    return body;
  }

  progress = () => {
    const { completed } = this.state;
    this.setState({ completed: completed >= 100 ? 0 : completed + 1 });
    };


  render() {
    return (
      <div>
        <GloblaStyle />
        <Table>
          <Thead>
            <tr className="tr">
              <td className="td">번호</td>
              <td className="td">이름</td>
              <td className="td">생년월일</td>
              <td className="td">성별</td>
              <td className="td">직업</td>
              <td className="td">설정</td>
            </tr>
          </Thead>
          <tbody>
            {this.state.customers ? this.state.customers.map(info => {
              return (
                <Customer
                  stateRefresh={this.stateRefresh}
                  key={info.id}
                  id={info.id}
                  name={info.NAME}
                  birthday={info.birthday}
                  gender={info.gender}
                  job={info.job}
                />
              )
            }) : ''}
          <tr>
            <CircularProgress className= {classes.progress} variant="determinate" value={this.state.completed} />
          </tr>

          </tbody>
        </Table>
        <CustomerAdd stateRefresh={this.stateRefresh} />
      </div>
    );
  }
}

export default App;
