import React, { useEffect, useState } from 'react';
import Customer from './components/Customer';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import { fade } from '@material-ui/core/styles/colorManipulator';
import Typography from '@material-ui/core/Typography';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import CustomerAdd from './components/CustomerAdd';
import { callApi } from './api/Api';
import Paging from './components/Paging';
import CircularProgress from '@material-ui/core/CircularProgress';




function App(props) {


    const [user, setUser] = useState({
        customers: ''
    })

    const [text, setText] = useState('');



    const stateRefresh = () => {
        setUser({
            customers: ''
        });

        callApi(setUser)
        setText('')

    }

    useEffect(() => {
        console.log("useEffect")
        callApi(setUser)

    }, [])

    const handleValueChange = (e) => {

        setText(e.target.value);

    }

    const searchEvent = (data) => {


        data = data.filter((c) => {
            if (c.NAME.indexOf(text) === 0) {

                return data;
            }
        });
        //console.log(data);

        pageEvent(data)
        return data.map((info) => {
            return (
                <Customer stateRefresh={stateRefresh} key={info.id} id={info.id} name={info.NAME} birthday={info.birthday} gender={info.gender} job={info.job} />
            )
        });

    }

    const pageEvent = (page) => {

        const num = page.length;
        console.log(num);
    }


    const cellList = ["번호", "이름", "생년월일", "성별", "직업", "설정"];

    return (
        <div>


            <AppBar position="static">
                <Toolbar>
                    <IconButton color="inherit" aria-label="Open drawer">
                        <MenuIcon />
                    </IconButton>

                    <Typography variant="h6" color="inherit" noWrap>
                        고객 관리 시스템
                    </Typography>

                    <div />
                    <div >
                        <div >
                            <SearchIcon />


                        </div>
                        <InputBase
                            placeholder="검색하기"


                            name="text"
                            value={text}
                            onChange={handleValueChange}
                        />
                    </div>

                </Toolbar>
            </AppBar>

            <Paper >
                <Table >
                    <TableHead>
                        <TableRow>
                            {cellList.map(c => {
                                return <TableCell >{c}</TableCell>
                            })}
                        </TableRow>
                    </TableHead>


                    <TableBody>

                        {
                            user.customers ? searchEvent(user.customers) : 

                            <TableCell colSpan="6" align="center">
                                <CircularProgress  />
                            </TableCell>
                        }
                    </TableBody>
                </Table>

                <Paging stateRefresh={stateRefresh} />

            </Paper>
            <div>
                <CustomerAdd stateRefresh={stateRefresh} />
            </div>
        </div>
    );

}

export default App;
