import axios from "axios"

export const callApi = (setUser, props) => {

    axios.get('/api/customers')
        .then(response => {
            setUser({ customers: response.data })
            console.log(response)
            console.log(response.data[props.id].NAME)
        }, error => {
            console.log(error);
        })
}

export const delApi = (props) => {


    const url = '/api/customers/';

        const data = {
             id : props.id
            }

        return axios.patch(url , data)
}

export const upApi = (props, user, birthday, gender, job) => {

      //axios.post 구성 url, data
      const url = '/api/customers/'; 

      const data = {
          id: props.id,
          NAME: user,
          birthday: birthday,
          gender: gender,
          job: job
      }
      console.log(data);

      return axios.put(url, data)
}

export const addApi = (name, birthday, gender, job) => {
    const url = '/api/customers';

    const data = {
        NAME: name,
        birthday: birthday,
        gender: gender,
        job: job
    }

    return axios.post(url, data)
}
