import React from 'react';
import styled from 'styled-components';
import Customer from './components/Customer';

const table = styled.table`
    border-collapse: collapse;
    margin: 25px 0;
    font-size: 0.9em;
    font-family: sans-serif;
    min-width: 400px;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);

    .thead tr {
        background-color: #009879;
        color: #ffffff;
        text-align: left;
    }

    .th{
        padding: 12px 15px;
    }

    .td{
        padding: 12px 15px;
    }
`;

const customer = [
  {
    'id': 1,
    'name': '홍길동',
    'birthday': '971202',
    'gender': '남자',
    'job': '학생'
  },
  {
    'id': 2,
    'name': '나동번',
    'birthday': '920202',
    'gender': '남자',
    'job': '프로그래머'
  },
  {
    'id': 3,
    'name': '박동민',
    'birthday': '921002',
    'gender': '남자',
    'job': '쿠팡맨'
  }
]

const App = () => {

  return (
    <div>
      {
        customer.map(info => {
          return (
            <Customer
              key={info.id}
              id={info.id}
              name={info.name}
              birthday={info.birthday}
              gender={info.gender}
              job={info.job}
            />
          )
        })}

    </div>
  )
}

export default App;
